    /// <summary>
    /// Gets the yearly sum of the notification passed in.
    /// </summary>
    /// <param name="notif"></param>
    /// <returns></returns>
    public int getYearlySumOfNotif(UserToNotifications notif)
    {
        return notif.Jan
            + notif.Feb
            + notif.Mar
            + notif.Apr
            + notif.May
            + notif.Jun
            + notif.Jul
            + notif.Aug
            + notif.Sep
            + notif.Oct
            + notif.Nov
            + notif.Dec;
    }

